spec :: Spec
spec = do
  it "parseInput" $ do
    parseInput simpleTestInput
      `shouldBe` simpleTestScanInput
    parseInput testInput `shouldBe` testScanInput

  it "toScans" $ do
    toScans simpleTestScanInput `shouldBe` [((0, 0, 0), [], [(0, 2, 5), (4, 1, 4), (3, 3, 2)]), ((0, 0, 0), [], [(-1, -1, -3), (-5, 0, 1), (-2, 1, 7)])]

  it "toVector" $ do
    toVector [0, 2, 5] `shouldBe` (0, 2, 5)
    toVector [4, 1, 4] `shouldBe` (4, 1, 4)
    toVector [3, 3, 2] `shouldBe` (3, 3, 2)

  it "toVectors" $ do
    toVectors [[0, 2, 5], [4, 1, 4], [3, 3, 2]] `shouldBe` [(0, 2, 5), (4, 1, 4), (3, 3, 2)]

  it "toScans" $ do
    toScans testScanInput `shouldBe` testScans

  it "mNegate" $ do
    mNegate ((0, 0, 0), (0, 0, 0), (0, 0, 0)) `shouldBe` ((0, 0, 0), (0, 0, 0), (0, 0, 0))
    mNegate ((1, 1, 1), (1, 1, 1), (1, 1, 1)) `shouldBe` ((-1, -1, -1), (-1, -1, -1), (-1, -1, -1))
    mNegate ((1, 0, 0), (0, 0, -1), (0, 1, 0)) `shouldBe` ((-1, 0, 0), (0, 0, 1), (0, -1, 0))

  it "toMatrix" $ do
    toMatrix (90, (1, 0, 0)) `shouldBe` ((1, 0, 0), (0, 0, -1), (0, 1, 0))
    toMatrix (-90, (1, 0, 0)) `shouldBe` ((1, 0, 0), (0, 0, 1), (0, -1, 0))
    toMatrix (90, (0, 1, 0)) `shouldBe` ((0, 0, 1), (0, 1, 0), (-1, 0, 0))
    toMatrix (-90, (0, 1, 0)) `shouldBe` ((0, 0, -1), (0, 1, 0), (1, 0, 0))
    toMatrix (90, (0, 0, 1)) `shouldBe` ((0, -1, 0), (1, 0, 0), (0, 0, 1))
    toMatrix (-90, (0, 0, 1)) `shouldBe` ((0, 1, 0), (-1, 0, 0), (0, 0, 1))

  it "rotateAll" $ do
    rotateAll (90, (1, 0, 0)) [(0, 2, 5), (4, 1, 4), (3, 3, 2)] `shouldBe` [(0, -5, 2), (4, -4, 1), (3, -2, 3)]
    rotateAll (-90, (1, 0, 0)) [(0, -5, 2), (4, -4, 1), (3, -2, 3)] `shouldBe` [(0, 2, 5), (4, 1, 4), (3, 3, 2)]
    rotateAll (90, (0, 1, 0)) [(0, 2, 5), (4, 1, 4), (3, 3, 2)] `shouldBe` [(5, 2, 0), (4, 1, -4), (2, 3, -3)]
    rotateAll (-90, (0, 1, 0)) [(5, 2, 0), (4, 1, -4), (2, 3, -3)] `shouldBe` [(0, 2, 5), (4, 1, 4), (3, 3, 2)]
    rotateAll (90, (0, 0, 1)) [(0, 2, 5), (4, 1, 4), (3, 3, 2)] `shouldBe` [(-2, 0, 5), (-1, 4, 4), (-3, 3, 2)]
    rotateAll (-90, (0, 0, 1)) [(-2, 0, 5), (-1, 4, 4), (-3, 3, 2)] `shouldBe` [(0, 2, 5), (4, 1, 4), (3, 3, 2)]

  it "rotationsX" $ do
    rotationsX simpleTestScan0
      `shouldBe` [ ((0, 0, 0), [], [(0, 2, 5), (4, 1, 4), (3, 3, 2)]),
                   ((0, 0, 0), [(90, (1, 0, 0))], [(0, -5, 2), (4, -4, 1), (3, -2, 3)]),
                   ((0, 0, 0), [(90, (1, 0, 0)), (90, (1, 0, 0))], [(0, -2, -5), (4, -1, -4), (3, -3, -2)]),
                   ((0, 0, 0), [(90, (1, 0, 0)), (90, (1, 0, 0)), (90, (1, 0, 0))], [(0, 5, -2), (4, 4, -1), (3, 2, -3)])
                 ]
  it "rotateScan" $ do
    rotateScan (360, (1, 0, 0)) ((0, 0, 0), [], [(0, 2, 5), (4, 1, 4), (3, 3, 2)]) `shouldBe` ((0, 0, 0), [(360, (1, 0, 0))], [(0, 2, 5), (4, 1, 4), (3, 3, 2)])

  it "findOverlapping" $ do
    findOverlapping testScan0 testScan1
      `shouldBe` Just (((-544, 627, 890), [], [(140, -39, 11), (16, 16, -1299), (1382, -1218, -1624), (154, 48, -97), (1081, 196, -432), (1029, -270, -1237), (889, -316, -1271), (1205, 189, -315), (1420, -1276, -1653), (1162, 197, -269), (-9, -972, -323), (70, -1207, -1557), (991, -298, -1208), (1128, -1495, -333), (0, 0, 0), (-20, -1019, -413), (89, -1356, -1618), (1436, -1151, -1574), (1233, -1472, -360), (121, 74, -1324), (537, -594, -819), (-86, -946, -511), (101, -1207, -1552), (1333, -1527, -339), (85, 80, -1291)]), ((-476, -619, 847), [(90, (0, 1, 0)), (90, (0, 1, 0))], [(1162, 197, -269), (1081, 196, -432), (991, -298, -1208), (140, -39, 11), (571, 481, -825), (0, 0, 0), (136, 1188, -1693), (1043, 980, -120), (16, 16, -1299), (1145, 1021, -247), (1205, 189, -315), (-24, 1380, -313), (154, 48, -97), (10, 1285, -1658), (47, 1211, -273), (121, 74, -1324), (1179, 1110, -1376), (148, 1304, -327), (889, -316, -1271), (85, 80, -1291), (1062, 1054, -290), (112, 1382, -1740), (1283, 1118, -1558), (1231, 973, -1466), (1029, -270, -1237)]))

  it "relativeScannerPositions" $ do
    relativeScannerPositions testScan0 testScan1 `shouldBe` Just relativeTestScan1
    relativeScannerPositions testScan0 testScan2 `shouldBe` Nothing
    relativeScannerPositions testScan0 testScan3 `shouldBe` Nothing
    relativeScannerPositions testScan0 testScan4 `shouldBe` Nothing
    relativeScannerPositions relativeTestScan1 testScan2 `shouldBe` Nothing
    relativeScannerPositions relativeTestScan1 testScan3 `shouldBe` Just relativeTestScan3
    relativeScannerPositions relativeTestScan1 testScan4 `shouldBe` Just relativeTestScan4
    relativeScannerPositions relativeTestScan3 testScan2 `shouldBe` Nothing
    relativeScannerPositions relativeTestScan4 testScan2 `shouldBe` Just relativeTestScan2

    relativeScannerPositions relativeTestScan4 testScan1 `shouldBe` Just relativeTestScan1
    relativeScannerPositions relativeTestScan2 testScan3 `shouldBe` Nothing
    relativeScannerPositions relativeTestScan2 testScan4 `shouldBe` Just relativeTestScan4

  it "howManyBeacons" $ do
    howManyBeacons testInput `shouldBe` 79

